-- Création de la base de données --
CREATE DATABASE IF NOT EXISTS testprojet;

-- Utilisation de la base de données --
USE testprojet;

-- Création de la table "animes" --
CREATE TABLE animes (
    id BIGINT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(150) NOT NULL,
    episode_number_total VARCHAR(10) NOT NULL,
    anime_synopsis TEXT NOT NULL,
    format TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

-- Création de la table "animeslists" --
CREATE TABLE animeslists (
    id BIGINT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
    current_episode VARCHAR(20) NOT NULL,
    last_released_episode VARCHAR(20) NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    user_id BIGINT UNSIGNED,
    anime_id BIGINT UNSIGNED,
    status_id BIGINT UNSIGNED,
    language_id BIGINT UNSIGNED
);

-- Création de la table "categories" --
CREATE TABLE categories (
    id BIGINT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(100) NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

-- Création de la table "episodes" --
CREATE TABLE episodes (
    id BIGINT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
    episode_name VARCHAR(200) NOT NULL,
    episode_number VARCHAR(20) NOT NULL,
    episode_synopsis TEXT NOT NULL,
    viewing_link VARCHAR(255),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    anime_id BIGINT UNSIGNED,
    language_id BIGINT UNSIGNED
);

-- Création de la table "languages" --
CREATE TABLE languages (
    id BIGINT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(100) NOT NULL,
    abbr VARCHAR(10) NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

-- Création de la table "statuses" --
CREATE TABLE statuses (
    id BIGINT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(100) NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

-- Création de la table "users" --
CREATE TABLE users (
    id BIGINT UNSIGNED PRIMARY KEY AUTO_INCREMENT,
    username VARCHAR(100) NOT NULL,
    password VARCHAR(255) NOT NULL,
    profil_picture  VARCHAR(255),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

CREATE TABLE anime_has_languages (
    PRIMARY KEY (anime_id, language_id),
    anime_id BIGINT UNSIGNED,
    language_id BIGINT UNSIGNED
);

CREATE TABLE categories_belongs (
    PRIMARY KEY (category_id, anime_id),
    category_id BIGINT UNSIGNED,
    anime_id BIGINT UNSIGNED
);

-- Ajout des clés étrangères entre les tables --

ALTER TABLE categories_belongs ADD FOREIGN KEY (anime_id) REFERENCES ANIMES (id);
ALTER TABLE categories_belongs ADD FOREIGN KEY (category_id) REFERENCES CATEGORIES (id);

ALTER TABLE anime_has_languages ADD FOREIGN KEY (language_id) REFERENCES LANGUAGES (id);
ALTER TABLE anime_has_languages ADD FOREIGN KEY (anime_id) REFERENCES ANIMES (id);

ALTER TABLE EPISODES ADD FOREIGN KEY (anime_id) REFERENCES ANIMES (id);
ALTER TABLE EPISODES ADD FOREIGN KEY (language_id) REFERENCES LANGUAGES (id);

ALTER TABLE ANIMESLISTS ADD FOREIGN KEY (language_id) REFERENCES LANGUAGES (id);
ALTER TABLE ANIMESLISTS ADD FOREIGN KEY (status_id) REFERENCES STATUSES (id);
ALTER TABLE ANIMESLISTS ADD FOREIGN KEY (anime_id) REFERENCES ANIMES (id);
ALTER TABLE ANIMESLISTS ADD FOREIGN KEY (user_id) REFERENCES USERS (id);

